class A { var x := 11 var y := 22 }
operator +(a:A, b:A) { var ret := A ret.x := a.x + b.x ret.y := a.y + b.y return ret }
var a := A var b := A var c := a + b stdout := c.x stdout := c.y


class A
{
  var value := 11
}

class B
{
  var value := 22
}

operator B(a:A)
{
  var ret := B
  ret.value := a.value
  return ret
}

var a := A()
var b:B := a
stdout := b.value
