class Counter
{
  var value := 0
  routine next() :: value += 1
}

Counter() =: var counter
echo counter.next()
echo counter.next()
echo counter.next()


class A {
  routine new {
    x := 3
    y := 6
  }

  var x:Int32
  var y:Int32
}

var a := A
echo a.x * a.y


class A {
  routine new(_x:Int32, _y:Int32) {
    x := _x
    y := _y
  }

  var x:Int32
  var y:Int32
}

var a:A := A(4, 7)
echo a.x * a.y


