case.code:

class A
{
  routine foo :: 10
}

class B : A
{
  routine bar :: foo + 25
}

B =: var o
echo o.foo
echo o.bar


class A
{
  protected routine foo :: 10
}

class B : A
{
  public routine bar :: foo + 25
}

B =: var o
echo o.bar


class A
{
  protected routine foo :: 10
}

class B : A
{
  public routine bar :: foo + 25
}

B =: var o
echo o.foo
echo o.bar


class A
{
  static routine foo :: 10
}

class B : A
{
  static routine bar :: A&.foo + 25
}

echo A&.foo
echo B&.bar


class A
{
  static routine foo :: 10
}

class B : A
{
  static routine bar :: B&.foo + 25
}

echo B&.foo
echo A&.bar

class A { routine foo :: 10 }
class B : A { routine foo :: 25 }
var a:A A =: a echo a.foo B =: a echo a.foo
B =: var b echo b.foo
