case.code:

routine foo!T(a:T):T {
  return a * a
}
echo foo!Int32(6)


routine foo!(A, B, R)(a:A, b:B):R {
  return a * b
}
echo foo!(Int32, Binary64, Binary64)(3, 5.5)


routine foo!T(v:T):void {
  var a:T := v
  var b:T := v
  loop i < 10 on var i := 0 by i += 1 {
    echo a
    var c:T := a
    a += b
    b := c
  }
}
foo!Int32(1)


routine foo(a) {
  return a * a
}
echo foo(6)


routine foo(a, b) {
  return a * b
}
echo foo(3, 5.5)


routine foo(v) {
  var a := v
  var b := v
  loop i < 10 on var i := 0 by i += 1 {
    echo a
    var c := a
    a += b
    b := c
  }
}
foo(1)


class O!T {
  var v:T

  routine foo(a:T):T {
    return a * v
  }
}
var o := O!Int32
o.v := 3
echo o.foo(6)


class O!(T) {
  var v:T

  routine new(v:T) {
    this.v := v
  }

  routine foo(a:T):T {
    return a * v
  }
}
var o := O(3)
echo o.foo(6)


class O!T {
  var v:T

  routine new(v:T) {
    this.v := v
  }

  routine foo!(A, R)(a:A):R {
    return a * v
  }
}
var o := O(3)
echo o.foo(5.5)


class O!T {
  var v:T

  routine new(v:T) {
    this.v := v
  }

  routine foo(a) {
    return a * v
  }
}
var o := O(3)
